import mongoose from "mongoose";

const userSchema = mongoose.Schema({
  email: {
    type: String,
    required: true,
    unique: true,
  },
  firstName: {
    type: String,
  },
  lastName: {
    type: String,
  },
  img: {
    type: String,
    default:
      "data:image/png;base64,",
  },
  password: {
    type: String,
    required: true,
  },
  phone: {
    type: Number,
  },
});

const User = mongoose.model("users", userSchema);
export default User;
